def queryString = mockRequest.getRequest().getQueryString()
log.info "QueryString: " + queryString


String[] fields = queryString.split("&")

String salesTransactionId = "";
String shoppingCartId = "";


fields.each{  

	def (value1, value2) = it.tokenize( '=' )
 	if (value1.equalsIgnoreCase("salestransactionid"))
		salesTransactionId = value2


	if (value1.equalsIgnoreCase("shopping-cart-id"))
		shoppingCartId = value2

}


log.info("salesTransactionid = " + salesTransactionId)
log.info("shopping-cart-id = " + shoppingCartId)

