log.info "inside ESMS MOCK"

/****************************************/
/* get shopping card id from path       */
/****************************************/
def requestPath = mockRequest.getPath()
log.info "Path: "+ requestPath

String[] pathFields = requestPath.split("/")

def shoppingCartId

pathFields.each{  
	if ( (it != "v2") &&  (it != "mso") &&  (it != "channelsalesmgmt") &&  (it != "enterprise-sales-management-service") &&  (it != "shopping-cart") &&  (it != "price"))
		shoppingCartId = it 
} 


/****************************************/
/* get salesTransactionID from Query    */
/****************************************/
def queryString = mockRequest.getRequest().getQueryString()
log.info "QueryString: " + queryString


String[] fields = queryString.split("&")

String salesTransactionId = ""; 


fields.each{  

	def (value1, value2) = it.tokenize( '=' )
 	if (value1.equalsIgnoreCase("salestransactionid"))
		salesTransactionId = value2
}


log.info("salesTransactionid = " + salesTransactionId)
log.info("shopping-cart-id = " + shoppingCartId)

if (salesTransactionId == "0001")
	return "1_ok_0001"
else if (salesTransactionId == "0002")
	return "2_ok_0002"
else if (salesTransactionId == "0007")
	return "7_ok_0007"
else if (salesTransactionId == "0008")
	return "8_ok_0008"

return "9_error_system_exception"

