def requestPath = mockRequest.getPath()
log.info("Sales  ----> ")
log.info "Path: "+ requestPath

log.info(requestPath)

def requestBody = mockRequest.getRequestContent()
log.info "Request body: " + requestBody
def String salesTransactionId = new groovy.json.JsonSlurper().parseText(requestBody).operationHeader.salesTransactionId;

log.info "salesTransactionId is : ${salesTransactionId}" 
requestContext.salesTransactionId = salesTransactionId

if (salesTransactionId == "0001" 
    || salesTransactionId == "0004"  
    || salesTransactionId == "0005" 
    || salesTransactionId == "0007"  ){
	return '1_ok_cart_with_customer_account'
} else if (salesTransactionId == "0002") {
	return '2_ok_cart_customer_manual_ind'
} else if (salesTransactionId == "0003")  {
	return '3_error_missing_required_input_field'
} else if (salesTransactionId == "0006")  {
	return '0006_response'
}else {
	return '9_error_system_exception'
}























