con:restMockAction.con:dispatchPath:
/**********************************************************************************************************************/
/*  soapui rest MOCK has bug with PUT and DELETE                                                                      */
/*  the only way to get the request is as per                                                                         */
/*  https://community.smartbear.com/t5/SoapUI-Pro/Mock-Service-mockRequest-requestContent-is-NULL-HTTP-PUT/td-p/42138 */
/*                                                                                                                    */
/**********************************************************************************************************************/


InputStream is = mockRequest.request.inputStream
BufferedReader br = new BufferedReader(new InputStreamReader(is, "UTF-8"));
StringBuilder sb = new StringBuilder()
while((s=br.readLine())!=null) {
sb.append(s)
}
String payLoad = sb.toString()
log.info "PayLoad -->" + payLoad

def String salesTransactionId = new groovy.json.JsonSlurper().parseText(payLoad).operationHeader.salesTransactionId;
log.info "salesTransactionId=>" + salesTransactionId

if (salesTransactionId == "0001" ){
	return '1_ok_0001'
} else if (salesTransactionId == "0006" ){
	return '2_ok_0006'
} else if (salesTransactionId == "0004" ){
	return '4_error_validate_offer_0004'
}
else if (salesTransactionId == "0007" ){
	return '3_ok_0007'
}else if (salesTransactionId == "0010" ){
	return '5_error_validate_disc_0010'
} else {
	return '9_error_system_exception'
}

