con:restMockAction.con:dispatchPath:
def requestPath = mockRequest.getPath()
log.info("Sales  ----> ")
log.info "Path: "+ requestPath

import java.text.SimpleDateFormat
def date = new Date()
//2018-06-20T11:30:13-0400
sdf = new SimpleDateFormat("yyyy-MM-dd")
sdf2  = new SimpleDateFormat("HH:mm:ss-0400")
def currentDate = sdf.format(date)
def currentTime = sdf2.format(date) 

requestContext.currnetDateTime = currentDate + "T" + currentTime
requestContext.locale = "en_CA"

log.info(requestPath)


if (requestPath != null){
	String[] fields = requestPath.split('/')

	salesId = ""
	isSalesItem = false

	fields.each{ 
		log.info(it)

		if (it != "v2" && it != "mss" && it != "channelsalesmgmt" && it != 'enterprise-sales-Service' && it != 'shopping-cart'){
			shopping_cart_id = it
		}

		if (it.equalsIgnoreCase("salesitem")){
			isSalesItem = true
		}
	}
}


if (shopping_cart_id == '0001')
	return "1_ok_0001"
else if (shopping_cart_id == '0006')
	return "2_ok_0006"
else if (shopping_cart_id == '0007')
	return "3_ok_0007"
else
	return "9_error_system_exception"

