	return "2a_finance"
} else if (salesTransactionId == "WL3"){
	return "3_error_message"
} else if (salesTransactionId == "WL4"){
	return "4a_salesOfferSummaryList"	
}  else {
	//	def allow_salesTransactionId = "WL1, WL2, WL3, WL4"
