	/**************************************/
	def String host = holder.getNodeValue("//host"); 

	//def host = context.expand('${#Project#host}');
	//log.info("HOST => " + host); 

	/**************************************/
	/* if no host override, use localhost */
	/**************************************/ 
	if (host == null || host.length() == 0)
		host = "localhost"; 

	/**************************************/
